mapper.select:
SELECT o. NAME AS leaveTypeName, o1. NAME AS statusName, u. NAME AS userName, l.* FROM `leave` l LEFT JOIN `user` u ON l.user_id = u.id LEFT JOIN ( SELECT di.* FROM dictionary_item di inner JOIN dictionary dic ON dic.id = di.dictionary_id AND dic. CODE = 'LeaveType' ) o ON o. CODE = l.leave_type LEFT JOIN ( SELECT di.* FROM dictionary_item di inner JOIN dictionary dic ON dic.id = di.dictionary_id AND dic. CODE = 'LeaveStatus' ) o1 ON o1. DATA = l.`status` where u.is_delete = 0 and u.is_enable = 1
and l.id = #{id}
order by l.create_time desc

SELECT o. NAME AS leaveTypeName, o1. NAME AS statusName, u. NAME AS userName, l.* FROM `leave` l LEFT JOIN `user` u ON l.user_id = u.id LEFT JOIN ( SELECT di.* FROM dictionary_item di inner JOIN dictionary dic ON dic.id = di.dictionary_id AND dic. CODE = 'LeaveType' ) o ON o. CODE = l.leave_type LEFT JOIN ( SELECT di.* FROM dictionary_item di inner JOIN dictionary dic ON dic.id = di.dictionary_id AND dic. CODE = 'LeaveStatus' ) o1 ON o1. DATA = l.`status` where u.is_delete = 0 and u.is_enable = 1
and l.user_id = #{userId}
ORDER BY l.create_time DESC

SELECT
*
FROM
`leave`
WHERE
user_id = #{userId}
AND status  != 9
AND NOT (
(
end_time < #{beginTime}
OR (begin_time > #{endTime})
)
)
